
/*!
 * qcCarousel - A lightweight carousel plugin for jQuery
 * https://www.jqueryscript.net/slider/image-video-carousel-qc.html
 */
(function ($) {
    $.fn.qcCarousel = function (options) {
        var settings = $.extend({
            autoPlay: false,
            delay: 3000,
            transition: 'slide',
        }, options);

        return this.each(function () {
            var $this = $(this);
            var $items = $this.children();
            var currentIndex = 0;

            function showSlide(index) {
                $items.hide();
                $items.eq(index).fadeIn();
            }

            function nextSlide() {
                currentIndex = (currentIndex + 1) % $items.length;
                showSlide(currentIndex);
            }

            if (settings.autoPlay) {
                setInterval(nextSlide, settings.delay);
            }

            showSlide(currentIndex);
        });
    };
}(jQuery));
